class Neuronio
{
    double pesoEntrada[100];
    int qEntrada;
    double valor=0;
    double threshold = 0.0;

    public:

    Neuronio(int qEntrada, double p[100] )
    {
        this->qEntrada=qEntrada;
        for (int i = 0 ; i < qEntrada; i++)
        {
            this->pesoEntrada[i]=p[i];
        }
    }

    double getPesoN(int n)
    {
        if(n<=qEntrada)
        {
            return pesoEntrada[n];
        }
        return 0.0;
    }

    setValor(double v)
    {
        this->valor = v;
    }
    double getValor()
    {
        return this->valor;
    }
    setThreshold(double v)
    {
        this->threshold = v;
    }
    double getThreshold()
    {
        return this->threshold;
    }

    int getTamanho()
    {
        return this->qEntrada;
    }

};

